{"query":"
query ($query: String, $type: MediaType) {
  Page {
    media(search: $query, type: $type) {
      id
      title {
        romaji
        english
        native
      }
      bannerImage
      coverImage {
        medium
        large
      }
      format
      type
      averageScore
      popularity
      episodes
      season
      hashtag
      isAdult
      startDate {
        year
        month
        day
      }
      endDate {
        year
        month
        day
      }
      relations {
        edges {
          relationType
        }
        nodes{
          id
        }
      }
    }
  }
}","variables":{"query":"Yedi Ölümcül Günah: Emirlerin Uyanışı","type":"ANIME"}}
